import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 4rem 2rem;
  position: relative;
  overflow: hidden;
`;

const HeroContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  text-align: center;
  z-index: 2;
  position: relative;
`;

const Title = styled(motion.h1)`
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 800;
  margin-bottom: 1.5rem;
  background: linear-gradient(135deg, #4fc3f7 0%, #7c4dff 50%, #581c87 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.3;
  text-shadow: 0 0 20px rgba(79, 195, 247, 0.2);
`;

const AuthorInfo = styled(motion.div)`
  font-size: clamp(0.85rem, 1.5vw, 1rem);
  color: ${props => props.theme.colors.textSecondary};
  margin-bottom: 3rem;
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.6;
`;

const AuthorName = styled.span`
  font-weight: 600;
  color: ${props => props.theme.colors.text};
`;

const Affiliation = styled.span`
  font-size: 0.9em;
  color: ${props => props.theme.colors.textSecondary};
`;


const ButtonGroup = styled(motion.div)`
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
`;

const Button = styled(motion.div)`
  padding: 1rem 2.5rem;
  border-radius: ${props => props.theme.borderRadius.lg};
  font-weight: 600;
  font-size: 1rem;
  font-family: ${props => props.theme.fonts.mono};
  text-transform: uppercase;
  letter-spacing: 0.5px;
  transition: all ${props => props.theme.transitions.normal};
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
  position: relative;
  overflow: hidden;
  text-decoration: none;

  span {
    font-size: 1.2em;
    filter: drop-shadow(0 0 8px currentColor);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left ${props => props.theme.transitions.normal};
  }

  &:hover::before {
    left: 100%;
  }
`;

const PrimaryButton = styled(Button)`
  background: linear-gradient(135deg, #1e3a8a 0%, #581c87 100%);
  color: ${props => props.theme.colors.text};
  border: 2px solid transparent;
  background-clip: padding-box;
  box-shadow: 0 4px 15px rgba(30, 58, 138, 0.3);

  &:hover {
    transform: translateY(-3px) scale(1.02);
    background: linear-gradient(135deg, #1e40af 0%, #6d28d9 100%);
    box-shadow: 0 8px 25px rgba(30, 58, 138, 0.5);
    color: #ffffff;
  }

  span {
    animation: bounce 2s infinite;
