import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { vscDarkPlus } from 'react-syntax-highlighter/dist/esm/styles/prism';

const DemoSection = styled.section`
  padding: 4rem 2rem;
  max-width: 1400px;
  margin: 0 auto;
  min-height: 100vh;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #ef4444 0%, #f97316 50%, #eab308 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled.p`
  text-align: center;
  color: ${props => props.theme.colors.textSecondary};
  font-size: 1.125rem;
  margin-bottom: 3rem;
`;

const DemoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;

  @media (max-width: 1200px) {
    grid-template-columns: 1fr;
  }
`;

const DemoContainer = styled.div`
  background-color: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: 2rem;
  border: 1px solid ${props => props.theme.colors.border};
  position: relative;
  overflow: hidden;
`;

const DemoHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
`;

const DemoTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
`;

const NetworkTopology = styled.div`
  padding: 2rem;
  text-align: center;
  background: linear-gradient(135deg, rgba(96, 165, 250, 0.05) 0%, rgba(167, 139, 250, 0.05) 100%);
  border-radius: ${props => props.theme.borderRadius.md};
  margin-bottom: 2rem;
`;

const NodeContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 2rem 0;
  position: relative;
`;

const NetworkNode = styled(motion.div)<{ nodeType: 'source' | 'attacker' | 'target' }>`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.875rem;
  position: relative;
  border: 3px solid;

  ${props => {
    switch (props.nodeType) {
      case 'source':
        return `
          background: linear-gradient(135deg, #60a5fa 0%, #3b82f6 100%);
          border-color: #60a5fa;
        `;
      case 'attacker':
        return `
          background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
          border-color: #ef4444;
        `;
      case 'target':
        return `
          background: linear-gradient(135deg, #10b981 0%, #059669 100%);
          border-color: #10b981;
        `;
    }
  }}
`;

const NodeLabel = styled.div`
  font-size: 0.75rem;
  margin-top: 0.5rem;
`;

const NodeIP = styled.div`
  font-size: 0.7rem;
  opacity: 0.9;
`;

const ConnectionLine = styled(motion.div)<{ isActive: boolean }>`
  position: absolute;
  height: 3px;
  background: ${props => props.isActive ? 
    'linear-gradient(90deg, #ef4444 0%, #f97316 50%, #eab308 100%)' : 
    '#374151'};
  top: 50%;
  transform: translateY(-50%);
  border-radius: 2px;

  &.line-1 {
    left: 140px;
    width: 120px;
  }

  &.line-2 {
    right: 140px;
    width: 120px;
  }
`;

const Terminal = styled.div`
  background-color: #1e1e1e;
  border-radius: ${props => props.theme.borderRadius.md};
  padding: 1.5rem;
  font-family: 'Consolas', 'Monaco', 'Courier New', monospace;
  font-size: 0.8rem;
  height: 400px;
  overflow-y: auto;
  position: relative;
`;

const TerminalHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid #374151;
`;

const TerminalDot = styled.div<{ color: string }>`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.color};
`;

const TerminalTitle = styled.span`
  color: #9ca3af;
  font-size: 0.75rem;
`;

const CommandLine = styled(motion.div)`
  color: #10b981;
  margin: 0.5rem 0;

  &::before {
    content: "$ ";
    color: #60a5fa;
  }
`;

const OutputLine = styled(motion.div)<{ type?: 'success' | 'error' | 'warning' | 'info' }>`
  color: ${props => {
    switch (props.type) {
      case 'success': return '#10b981';
      case 'error': return '#ef4444';
      case 'warning': return '#f59e0b';
      case 'info': return '#60a5fa';
      default: return '#d1d5db';
    }
  }};
  margin: 0.25rem 0;
  font-size: 0.75rem;
`;

const ControlPanel = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
`;

const ControlButton = styled.button<{ variant?: 'primary' | 'danger' | 'success' }>`
  padding: 0.5rem 1rem;
  border-radius: ${props => props.theme.borderRadius.md};
  border: none;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  ${props => {
    switch (props.variant) {
      case 'primary':
        return `
          background: linear-gradient(135deg, #60a5fa 0%, #3b82f6 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(96, 165, 250, 0.3); }
        `;
      case 'danger':
        return `
          background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3); }
        `;
      case 'success':
        return `
          background: linear-gradient(135deg, #10b981 0%, #059669 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3); }
        `;
      default:
        return `
          background-color: ${props.theme.colors.surface};
          color: ${props.theme.colors.text};
          border: 1px solid ${props.theme.colors.border};
          &:hover { background-color: ${props.theme.colors.surfaceLight}; }
        `;
    }
  }}

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const StatusIndicator = styled.div<{ status: 'idle' | 'scanning' | 'spoofing' | 'capturing' }>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: ${props => props.theme.borderRadius.md};
  font-size: 0.875rem;
  font-weight: 600;

  ${props => {
    switch (props.status) {
      case 'idle':
        return `
          background-color: rgba(107, 114, 128, 0.1);
          color: #6b7280;
        `;
      case 'scanning':
        return `
          background-color: rgba(59, 130, 246, 0.1);
          color: #3b82f6;
        `;
      case 'spoofing':
        return `
          background-color: rgba(239, 68, 68, 0.1);
          color: #ef4444;
        `;
      case 'capturing':
        return `
          background-color: rgba(16, 185, 129, 0.1);
          color: #10b981;
        `;
    }
  }}
`;

const StatusDot = styled.div<{ isActive: boolean }>`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background-color: currentColor;
  animation: ${props => props.isActive ? 'pulse 2s infinite' : 'none'};

  @keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
  }
`;

// Attack simulation data
const networkScanSteps = [
  { command: "nmap -sn 192.168.2.0/24", description: "Scanning network for active hosts" },
  { command: "ip route show", description: "Checking network topology" },
  { command: "arp -a | grep \"192.168.2\"", description: "Discovering ARP table entries" }
];

const arpSpoofingSteps = [
  { command: "echo 1 > /proc/sys/net/ipv4/ip_forward", description: "Enabling IP forwarding" },
  { command: "ettercap -T -i eth0 -M arp:remote /192.168.2.100// /192.168.2.150//", description: "Starting ARP spoofing attack" }
];

const trafficCaptureSteps = [
  { command: "tcpdump -i eth0 -w traffic_capture.pcap host 192.168.2.100 and host 192.168.2.150", description: "Capturing network traffic" },
  { command: "tcpdump -A -s 0 port 80 and host 192.168.2.150", description: "Monitoring HTTP traffic" }
];

export const TrafficHijackDemo: React.FC = () => {
  const [currentPhase, setCurrentPhase] = useState<'idle' | 'scanning' | 'spoofing' | 'capturing'>('idle');
  const [terminalOutput, setTerminalOutput] = useState<Array<{ type: 'command' | 'output', content: string, outputType?: 'success' | 'error' | 'warning' | 'info' }>>([]);
  const [isAttackActive, setIsAttackActive] = useState(false);
  const terminalRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [terminalOutput]);

  const addTerminalLine = (type: 'command' | 'output', content: string, outputType?: 'success' | 'error' | 'warning' | 'info') => {
    setTerminalOutput(prev => [...prev, { type, content, outputType }]);
  };

  const simulateNetworkScan = async () => {
    setCurrentPhase('scanning');
    addTerminalLine('output', '=== Starting Network Discovery ===', 'info');

    for (const step of networkScanSteps) {
      await new Promise(resolve => setTimeout(resolve, 1000));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 500));

      if (step.command.includes('nmap')) {
        addTerminalLine('output', 'Starting Nmap 7.80 ( https://nmap.org ) at 2025-09-18 01:04 UTC', 'info');
        addTerminalLine('output', 'Nmap scan report for 192.168.2.100', 'success');
        addTerminalLine('output', 'Host is up (0.00038s latency).', 'success');
        addTerminalLine('output', 'Nmap scan report for 192.168.2.150', 'success');
        addTerminalLine('output', 'Host is up (0.00038s latency).', 'success');
        addTerminalLine('output', 'Nmap done: 2 IP addresses (2 hosts up) scanned in 0.18 seconds', 'info');
      } else if (step.command.includes('ip route')) {
        addTerminalLine('output', 'default via 192.168.2.1 dev eth0', 'info');
        addTerminalLine('output', '192.168.2.0/24 dev eth0 proto kernel scope link src 192.168.2.101', 'info');
      } else if (step.command.includes('arp')) {
        addTerminalLine('output', '192.168.2.100 at 02:42:c0:a8:02:64 [ether] on eth0', 'info');
        addTerminalLine('output', '192.168.2.150 at 02:42:c0:a8:02:96 [ether] on eth0', 'info');
      }
    }

    addTerminalLine('output', '‚úÖ Network topology discovered successfully', 'success');
  };

  const simulateArpSpoofing = async () => {
    setCurrentPhase('spoofing');
    addTerminalLine('output', '=== Starting ARP Spoofing Attack ===', 'info');

    for (const step of arpSpoofingSteps) {
      await new Promise(resolve => setTimeout(resolve, 1000));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 500));

      if (step.command.includes('ip_forward')) {
        addTerminalLine('output', 'IP forwarding enabled', 'success');
      } else if (step.command.includes('ettercap')) {
        addTerminalLine('output', 'ettercap 0.8.3.1 copyright 2001-2020 Ettercap Development Team', 'info');
        addTerminalLine('output', 'Listening on eth0... (Ethernet)', 'info');
        addTerminalLine('output', 'Starting Unified sniffing...', 'warning');
        addTerminalLine('output', 'ARP poisoner activated.', 'warning');
        addTerminalLine('output', 'Intercepting traffic between 192.168.2.100 and 192.168.2.150', 'warning');
        setIsAttackActive(true);
      }
    }

    addTerminalLine('output', 'üî• ARP spoofing attack is now active', 'warning');
  };

  const simulateTrafficCapture = async () => {
    setCurrentPhase('capturing');
    addTerminalLine('output', '=== Starting Traffic Monitoring ===', 'info');

    for (const step of trafficCaptureSteps) {
      await new Promise(resolve => setTimeout(resolve, 1000));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 500));

      if (step.command.includes('tcpdump -i')) {
        addTerminalLine('output', 'tcpdump: listening on eth0, link-type EN10MB (Ethernet)', 'info');
        addTerminalLine('output', 'Capturing packets to traffic_capture.pcap...', 'success');
      } else if (step.command.includes('tcpdump -A')) {
        addTerminalLine('output', 'tcpdump: listening on eth0, link-type EN10MB (Ethernet)', 'info');
        addTerminalLine('output', '01:07:42.123456 IP 192.168.2.100.52345 > 192.168.2.150.80: HTTP GET /', 'warning');
        addTerminalLine('output', 'Host: target-server', 'warning');
        addTerminalLine('output', 'User-Agent: Mozilla/5.0...', 'warning');
        addTerminalLine('output', 'üì¶ HTTP traffic intercepted successfully', 'success');
      }
    }

    addTerminalLine('output', 'üéØ Traffic capture active - monitoring all communications', 'success');
  };

  const resetDemo = () => {
    setCurrentPhase('idle');
    setTerminalOutput([]);
    setIsAttackActive(false);
  };

  const startFullAttack = async () => {
    resetDemo();
    await simulateNetworkScan();
    await simulateArpSpoofing();
    await simulateTrafficCapture();
  };

  const getStatusText = () => {
    switch (currentPhase) {
      case 'scanning': return 'Network Scanning';
      case 'spoofing': return 'ARP Spoofing';
      case 'capturing': return 'Traffic Monitoring';
      default: return 'Ready';
    }
  };

  return (
    <DemoSection id="traffic-hijack">
      <SectionTitle>Traffic Hijacking Attack Demo</SectionTitle>
      <SectionSubtitle>
        Demonstration of Man-in-the-Middle attack using ARP spoofing and traffic interception
      </SectionSubtitle>

      <DemoGrid>
        {/* Network Topology Visualization */}
        <DemoContainer>
          <DemoHeader>
            <DemoTitle>Network Topology</DemoTitle>
            <StatusIndicator status={currentPhase}>
              <StatusDot isActive={currentPhase !== 'idle'} />
              {getStatusText()}
            </StatusIndicator>
          </DemoHeader>

          <NetworkTopology>
            <NodeContainer>
              <NetworkNode 
                nodeType="source"
                animate={{ 
                  scale: currentPhase === 'scanning' ? [1, 1.1, 1] : 1,
                  borderColor: currentPhase === 'scanning' ? ['#60a5fa', '#3b82f6', '#60a5fa'] : '#60a5fa'
                }}
                transition={{ duration: 2, repeat: currentPhase === 'scanning' ? Infinity : 0 }}
              >
                <div>Source</div>
                <NodeLabel>SSH Server</NodeLabel>
                <NodeIP>192.168.2.100</NodeIP>
              </NetworkNode>

              <ConnectionLine 
                className="line-1" 
                isActive={isAttackActive}
                animate={{ 
                  opacity: isAttackActive ? [0.5, 1, 0.5] : 0.3 
                }}
                transition={{ duration: 1.5, repeat: isAttackActive ? Infinity : 0 }}
              />

              <NetworkNode 
                nodeType="attacker"
                animate={{ 
                  scale: currentPhase === 'spoofing' ? [1, 1.2, 1] : 1,
                  borderColor: currentPhase === 'spoofing' ? ['#ef4444', '#dc2626', '#ef4444'] : '#ef4444'
                }}
                transition={{ duration: 1.5, repeat: currentPhase === 'spoofing' ? Infinity : 0 }}
              >
                <div>Attacker</div>
                <NodeLabel>MITM Node</NodeLabel>
                <NodeIP>192.168.2.101</NodeIP>
              </NetworkNode>

              <ConnectionLine 
                className="line-2" 
                isActive={isAttackActive}
                animate={{ 
                  opacity: isAttackActive ? [0.5, 1, 0.5] : 0.3 
                }}
                transition={{ duration: 1.5, repeat: isAttackActive ? Infinity : 0 }}
              />

              <NetworkNode 
                nodeType="target"
                animate={{ 
                  scale: currentPhase === 'capturing' ? [1, 1.1, 1] : 1,
                  borderColor: currentPhase === 'capturing' ? ['#10b981', '#059669', '#10b981'] : '#10b981'
                }}
                transition={{ duration: 2, repeat: currentPhase === 'capturing' ? Infinity : 0 }}
              >
                <div>Target</div>
                <NodeLabel>HTTP Client</NodeLabel>
                <NodeIP>192.168.2.150</NodeIP>
              </NetworkNode>
            </NodeContainer>
          </NetworkTopology>

          <ControlPanel>
            <ControlButton variant="primary" onClick={startFullAttack} disabled={currentPhase !== 'idle'}>
              üöÄ Start Attack
            </ControlButton>
            <ControlButton onClick={simulateNetworkScan} disabled={currentPhase !== 'idle'}>
              üîç Network Scan
            </ControlButton>
            <ControlButton variant="danger" onClick={simulateArpSpoofing} disabled={currentPhase !== 'idle'}>
              ‚ö° ARP Spoofing
            </ControlButton>
            <ControlButton variant="success" onClick={simulateTrafficCapture} disabled={currentPhase !== 'idle'}>
              üì° Monitor Traffic
            </ControlButton>
            <ControlButton onClick={resetDemo}>
              üîÑ Reset
            </ControlButton>
          </ControlPanel>
        </DemoContainer>

        {/* Terminal Output */}
        <DemoContainer>
          <DemoHeader>
            <DemoTitle>Attack Terminal</DemoTitle>
          </DemoHeader>

          <Terminal ref={terminalRef}>
            <TerminalHeader>
              <TerminalDot color="#ef4444" />
              <TerminalDot color="#f59e0b" />
              <TerminalDot color="#10b981" />
              <TerminalTitle>root@attacker:/tmp/network_capture</TerminalTitle>
            </TerminalHeader>

            <AnimatePresence>
              {terminalOutput.map((line, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  {line.type === 'command' ? (
                    <CommandLine>{line.content}</CommandLine>
                  ) : (
                    <OutputLine type={line.outputType}>{line.content}</OutputLine>
                  )}
                </motion.div>
              ))}
            </AnimatePresence>

            {currentPhase !== 'idle' && (
              <motion.div
                animate={{ opacity: [1, 0.5, 1] }}
                transition={{ duration: 1, repeat: Infinity }}
                style={{ color: '#60a5fa', marginTop: '1rem' }}
              >
                ‚ñã
              </motion.div>
            )}
          </Terminal>
        </DemoContainer>
      </DemoGrid>
    </DemoSection>
  );
};
