import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const DemoSection = styled.section`
  padding: 4rem 2rem;
  max-width: 1400px;
  margin: 0 auto;
  min-height: 100vh;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #8b5cf6 0%, #ec4899 50%, #06b6d4 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled.p`
  text-align: center;
  color: ${props => props.theme.colors.textSecondary};
  font-size: 1.125rem;
  margin-bottom: 3rem;
`;

const DemoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;

  @media (max-width: 1200px) {
    grid-template-columns: 1fr;
  }
`;

const DemoContainer = styled.div`
  background-color: ${props => props.theme.colors.surface};
  border-radius: ${props => props.theme.borderRadius.lg};
  padding: 2rem;
  border: 1px solid ${props => props.theme.colors.border};
  position: relative;
  overflow: hidden;
`;

const DemoHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
`;

const DemoTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
`;

const DNSTopology = styled.div`
  padding: 2rem;
  text-align: center;
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.05) 0%, rgba(236, 72, 153, 0.05) 100%);
  border-radius: ${props => props.theme.borderRadius.md};
  margin-bottom: 2rem;
`;

const DHCPContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin: 2rem 0;
`;

const DHCPFlow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  position: relative;
`;

const NetworkNode = styled(motion.div)<{ nodeType: 'client' | 'dhcp' | 'dns' }>`
  width: 100px;
  height: 100px;
  border-radius: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.75rem;
  position: relative;
  border: 3px solid;

  ${props => {
    switch (props.nodeType) {
      case 'client':
        return `
          background: linear-gradient(135deg, #06b6d4 0%, #0891b2 100%);
          border-color: #06b6d4;
        `;
      case 'dhcp':
        return `
          background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
          border-color: #8b5cf6;
        `;
      case 'dns':
        return `
          background: linear-gradient(135deg, #ec4899 0%, #db2777 100%);
          border-color: #ec4899;
        `;
    }
  }}
`;

const NodeLabel = styled.div`
  font-size: 0.7rem;
  margin-top: 0.5rem;
`;

const NodeIP = styled.div`
  font-size: 0.6rem;
  opacity: 0.9;
`;

const FlowArrow = styled(motion.div)<{ isActive: boolean }>`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  height: 3px;
  background: ${props => props.isActive ? 
    'linear-gradient(90deg, #8b5cf6 0%, #ec4899 50%, #06b6d4 100%)' : 
    '#374151'};
  border-radius: 2px;

  &::after {
    content: '';
    position: absolute;
    right: -8px;
    top: -5px;
    width: 0;
    height: 0;
    border-left: 8px solid ${props => props.isActive ? '#06b6d4' : '#374151'};
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
  }

  &.arrow-1 {
    left: 120px;
    width: 80px;
  }

  &.arrow-2 {
    right: 120px;
    width: 80px;
  }
`;

const StepIndicator = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 1rem 0;
`;

const Step = styled(motion.div)<{ active: boolean }>`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.active ? '#8b5cf6' : '#374151'};
  transition: all 0.3s ease;
`;

const Terminal = styled.div`
  background-color: #1e1e1e;
  border-radius: ${props => props.theme.borderRadius.md};
  padding: 1.5rem;
  font-family: 'Consolas', 'Monaco', 'Courier New', monospace;
  font-size: 0.8rem;
  height: 400px;
  overflow-y: auto;
  position: relative;
`;

const TerminalHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid #374151;
`;

const TerminalDot = styled.div<{ color: string }>`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.color};
`;

const TerminalTitle = styled.span`
  color: #9ca3af;
  font-size: 0.75rem;
`;

const CommandLine = styled(motion.div)`
  color: #10b981;
  margin: 0.5rem 0;

  &::before {
    content: "$ ";
    color: #8b5cf6;
  }
`;

const OutputLine = styled(motion.div)<{ type?: 'success' | 'error' | 'warning' | 'info' }>`
  color: ${props => {
    switch (props.type) {
      case 'success': return '#10b981';
      case 'error': return '#ef4444';
      case 'warning': return '#f59e0b';
      case 'info': return '#06b6d4';
      default: return '#d1d5db';
    }
  }};
  margin: 0.25rem 0;
  font-size: 0.75rem;
`;

const ControlPanel = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;
`;

const ControlButton = styled.button<{ variant?: 'primary' | 'danger' | 'success' | 'warning' }>`
  padding: 0.5rem 1rem;
  border-radius: ${props => props.theme.borderRadius.md};
  border: none;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;

  ${props => {
    switch (props.variant) {
      case 'primary':
        return `
          background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(139, 92, 246, 0.3); }
        `;
      case 'danger':
        return `
          background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3); }
        `;
      case 'success':
        return `
          background: linear-gradient(135deg, #10b981 0%, #059669 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(16, 185, 129, 0.3); }
        `;
      case 'warning':
        return `
          background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
          color: white;
          &:hover { transform: translateY(-1px); box-shadow: 0 4px 12px rgba(245, 158, 11, 0.3); }
        `;
      default:
        return `
          background-color: ${props.theme.colors.surface};
          color: ${props.theme.colors.text};
          border: 1px solid ${props.theme.colors.border};
          &:hover { background-color: ${props.theme.colors.surfaceLight}; }
        `;
    }
  }}

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const StatusIndicator = styled.div<{ status: 'idle' | 'dhcp' | 'dns' | 'spoofing' | 'monitoring' }>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  border-radius: ${props => props.theme.borderRadius.md};
  font-size: 0.875rem;
  font-weight: 600;

  ${props => {
    switch (props.status) {
      case 'idle':
        return `
          background-color: rgba(107, 114, 128, 0.1);
          color: #6b7280;
        `;
      case 'dhcp':
        return `
          background-color: rgba(139, 92, 246, 0.1);
          color: #8b5cf6;
        `;
      case 'dns':
        return `
          background-color: rgba(236, 72, 153, 0.1);
          color: #ec4899;
        `;
      case 'spoofing':
        return `
          background-color: rgba(239, 68, 68, 0.1);
          color: #ef4444;
        `;
      case 'monitoring':
        return `
          background-color: rgba(16, 185, 129, 0.1);
          color: #10b981;
        `;
    }
  }}
`;

const StatusDot = styled.div<{ isActive: boolean }>`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background-color: currentColor;
  animation: ${props => props.isActive ? 'pulse 2s infinite' : 'none'};

  @keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
  }
`;

const InfoPanel = styled.div`
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.05) 0%, rgba(236, 72, 153, 0.05) 100%);
  border-radius: ${props => props.theme.borderRadius.md};
  padding: 1rem;
  margin-bottom: 1rem;
`;

const InfoTitle = styled.h4`
  color: ${props => props.theme.colors.text};
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const InfoText = styled.p`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.875rem;
  line-height: 1.5;
`;

// Attack simulation data
const dhcpPoisoningSteps = [
  { command: "apt-get update && apt-get install -y dnsmasq", description: "Installing dnsmasq for DHCP/DNS services" },
  { command: "systemctl stop systemd-resolved", description: "Stopping system DNS resolver" },
  { command: "cat > /etc/dnsmasq.conf", description: "Configuring DHCP server" }
];

const dnsConfigSteps = [
  { command: "echo 'dhcp-range=192.168.2.50,192.168.2.150,12h' >> /etc/dnsmasq.conf", description: "Setting DHCP range" },
  { command: "echo 'dhcp-option=6,192.168.2.101' >> /etc/dnsmasq.conf", description: "Setting DNS server option" },
  { command: "echo 'address=/target-server/192.168.2.150' >> /etc/dnsmasq.conf", description: "Adding DNS spoofing entry" }
];

const attackExecutionSteps = [
  { command: "systemctl restart dnsmasq", description: "Starting malicious DHCP/DNS server" },
  { command: "systemctl status dnsmasq", description: "Verifying service status" },
  { command: "tail -f /var/log/dnsmasq.log", description: "Monitoring DNS queries" }
];

export const DNSHijackDemo: React.FC = () => {
  const [currentPhase, setCurrentPhase] = useState<'idle' | 'dhcp' | 'dns' | 'spoofing' | 'monitoring'>('idle');
  const [currentStep, setCurrentStep] = useState(0);
  const [terminalOutput, setTerminalOutput] = useState<Array<{ type: 'command' | 'output', content: string, outputType?: 'success' | 'error' | 'warning' | 'info' }>>([]);
  const [isAttackActive, setIsAttackActive] = useState(false);
  const [dhcpFlowActive, setDhcpFlowActive] = useState(false);
  const terminalRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [terminalOutput]);

  const addTerminalLine = (type: 'command' | 'output', content: string, outputType?: 'success' | 'error' | 'warning' | 'info') => {
    setTerminalOutput(prev => [...prev, { type, content, outputType }]);
  };

  const simulateDHCPSetup = async () => {
    setCurrentPhase('dhcp');
    setCurrentStep(1);
    addTerminalLine('output', '=== Setting up Malicious DHCP Server ===', 'info');

    for (const step of dhcpPoisoningSteps) {
      await new Promise(resolve => setTimeout(resolve, 1500));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 800));

      if (step.command.includes('apt-get')) {
        addTerminalLine('output', 'Reading package lists... Done', 'info');
        addTerminalLine('output', 'Building dependency tree... Done', 'info');
        addTerminalLine('output', 'dnsmasq is already the newest version (2.90-0ubuntu0.22.04.1).', 'success');
        addTerminalLine('output', '0 upgraded, 0 newly installed, 0 to remove and 0 not upgraded.', 'success');
      } else if (step.command.includes('systemctl stop')) {
        addTerminalLine('output', 'Stopped system DNS resolver', 'warning');
      } else if (step.command.includes('cat >')) {
        addTerminalLine('output', 'Creating dnsmasq configuration...', 'info');
      }
    }

    addTerminalLine('output', '‚úÖ DHCP server configuration ready', 'success');
  };

  const simulateDNSConfig = async () => {
    setCurrentPhase('dns');
    setCurrentStep(2);
    addTerminalLine('output', '=== Configuring DNS Spoofing ===', 'info');

    for (const step of dnsConfigSteps) {
      await new Promise(resolve => setTimeout(resolve, 1200));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 600));

      if (step.command.includes('dhcp-range')) {
        addTerminalLine('output', 'DHCP range: 192.168.2.50 - 192.168.2.150', 'info');
      } else if (step.command.includes('dhcp-option')) {
        addTerminalLine('output', 'DNS server set to: 192.168.2.101 (attacker)', 'warning');
      } else if (step.command.includes('address=')) {
        addTerminalLine('output', 'DNS spoofing: target-server ‚Üí 192.168.2.150', 'warning');
      }
    }

    addTerminalLine('output', 'üéØ DNS spoofing configuration complete', 'warning');
  };

  const simulateAttackExecution = async () => {
    setCurrentPhase('spoofing');
    setCurrentStep(3);
    setDhcpFlowActive(true);
    addTerminalLine('output', '=== Launching Attack ===', 'info');

    for (const step of attackExecutionSteps) {
      await new Promise(resolve => setTimeout(resolve, 1500));
      addTerminalLine('command', step.command);
      await new Promise(resolve => setTimeout(resolve, 800));

      if (step.command.includes('restart dnsmasq')) {
        addTerminalLine('output', 'Starting DNS forwarder and DHCP server dnsmasq', 'success');
        addTerminalLine('output', '[ OK ]', 'success');
