import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const StarfieldContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: -1;
  overflow: hidden;
`;

const StarfieldCanvas = styled.canvas`
  width: 100%;
  height: 100%;
  opacity: 0.8;
`;

interface Star {
  x: number;
  y: number;
  size: number;
  opacity: number;
  twinkleSpeed: number;
  color: string;
  phase: number;
}

interface StarfieldBackgroundProps {
  starCount?: number;
  twinkleSpeed?: number;
}

export const StarfieldBackground: React.FC<StarfieldBackgroundProps> = ({ 
  starCount = 200, 
  twinkleSpeed = 0.02 
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationRef = useRef<number>();
  const starsRef = useRef<Star[]>([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      initializeStars();
    };

    // Star colors - different shades of blue and purple
    const starColors = [
      '#4fc3f7', // Primary blue
      '#7c4dff', // Purple
      '#00bcd4', // Cyan
      '#81c784', // Light green
      '#fff3e0', // Warm white
      '#e1bee7', // Light purple
    ];

    // Initialize stars
    const initializeStars = () => {
      starsRef.current = [];
      for (let i = 0; i < starCount; i++) {
        starsRef.current.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          size: Math.random() * 3 + 0.5,
          opacity: Math.random(),
          twinkleSpeed: (Math.random() * twinkleSpeed + twinkleSpeed * 0.5),
          color: starColors[Math.floor(Math.random() * starColors.length)],
          phase: Math.random() * Math.PI * 2,
        });
      }
    };

    // Animation loop
    const animate = () => {
      // Clear canvas with slight trail effect
      ctx.fillStyle = 'rgba(0, 0, 0, 0.02)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw and update stars
      starsRef.current.forEach(star => {
        // Update twinkle phase
        star.phase += star.twinkleSpeed;

        // Calculate twinkling opacity
        const twinkle = (Math.sin(star.phase) + 1) / 2;
        const currentOpacity = star.opacity * twinkle;

        // Create gradient for glow effect
        const gradient = ctx.createRadialGradient(
          star.x, star.y, 0,
          star.x, star.y, star.size * 3
        );
        gradient.addColorStop(0, `${star.color}${Math.floor(currentOpacity * 255).toString(16).padStart(2, '0')}`);
        gradient.addColorStop(0.3, `${star.color}${Math.floor(currentOpacity * 128).toString(16).padStart(2, '0')}`);
        gradient.addColorStop(1, 'transparent');

        // Draw star with glow
        ctx.fillStyle = gradient;
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size * 3, 0, Math.PI * 2);
        ctx.fill();

        // Draw bright center
        ctx.fillStyle = `${star.color}${Math.floor(currentOpacity * 255).toString(16).padStart(2, '0')}`;
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
        ctx.fill();

        // Add sparkle effect for larger stars
        if (star.size > 2 && currentOpacity > 0.7) {
          const sparkleLength = star.size * 2;
          ctx.strokeStyle = `${star.color}${Math.floor(currentOpacity * 128).toString(16).padStart(2, '0')}`;
          ctx.lineWidth = 0.5;

          // Vertical sparkle
          ctx.beginPath();
          ctx.moveTo(star.x, star.y - sparkleLength);
          ctx.lineTo(star.x, star.y + sparkleLength);
          ctx.stroke();

          // Horizontal sparkle
          ctx.beginPath();
          ctx.moveTo(star.x - sparkleLength, star.y);
          ctx.lineTo(star.x + sparkleLength, star.y);
          ctx.stroke();
        }
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [starCount, twinkleSpeed]);

  return (
    <StarfieldContainer>
      <StarfieldCanvas ref={canvasRef} />
    </StarfieldContainer>
  );
};

export default StarfieldBackground;
