export const theme = {
  colors: {
    // Matrix/Hacker inspired colors
    primary: '#00ff41', // Matrix green
    secondary: '#ff0080', // Cyber pink
    accent: '#00d4aa', // Cyan accent
    danger: '#ff3333', // Alert red
    success: '#00ff41', // Matrix green
    warning: '#ffaa00', // Amber warning

    // Background layers for depth
    background: '#000000', // Pure black
    surface: '#0d1117', // GitHub dark
    surfaceLight: '#161b22', // Elevated surface
    surfaceHover: '#21262d', // Hover state

    // Text hierarchy
    text: '#ffffff', // Pure white
    textPrimary: '#00ff41', // Matrix green text
    textSecondary: '#8b949e', // Muted gray
    textTertiary: '#6e7681', // Even more muted

    // Border and dividers
    border: '#30363d', // Subtle borders
    borderBright: '#00ff41', // Highlighted borders

    // Special effects
    glow: 'rgba(0, 255, 65, 0.5)', // Green glow
    glowPink: 'rgba(255, 0, 128, 0.5)', // Pink glow
    glowCyan: 'rgba(0, 212, 170, 0.5)', // Cyan glow

    // Terminal colors
    terminal: {
      background: '#0c0c0c',
      green: '#00ff41',
      red: '#ff6b6b',
      yellow: '#ffd93d',
      blue: '#6bceff',
      purple: '#d291ff',
      cyan: '#4ecdc4',
    },
  },
  spacing: {
    xs: '0.25rem',
    sm: '0.5rem',
    md: '1rem',
    lg: '1.5rem',
    xl: '2rem',
    xxl: '3rem',
  },
  borderRadius: {
    sm: '0.25rem',
    md: '0.5rem',
    lg: '1rem',
    xl: '1.5rem',
  },
  shadows: {
    sm: '0 1px 2px rgba(0, 0, 0, 0.8)',
    md: '0 4px 6px rgba(0, 0, 0, 0.8)',
    lg: '0 10px 15px rgba(0, 0, 0, 0.8)',
    xl: '0 20px 25px rgba(0, 0, 0, 0.8)',
    // Glow effects
    glow: '0 0 20px rgba(0, 255, 65, 0.5)',
    glowSm: '0 0 10px rgba(0, 255, 65, 0.3)',
    glowLg: '0 0 30px rgba(0, 255, 65, 0.7)',
    glowPink: '0 0 20px rgba(255, 0, 128, 0.5)',
    glowCyan: '0 0 20px rgba(0, 212, 170, 0.5)',
  },
  transitions: {
    fast: '0.15s ease',
    normal: '0.3s ease',
    slow: '0.5s ease',
    bounce: 'cubic-bezier(0.68, -0.55, 0.265, 1.55)',
  },
  fonts: {
    mono: '"JetBrains Mono", "Fira Code", "Consolas", "Monaco", monospace',
    sans: '"Inter", "Roboto", "Segoe UI", sans-serif',
    display: '"Orbitron", "Exo 2", sans-serif',
  },
  effects: {
    scan: 'linear-gradient(90deg, transparent, rgba(0, 255, 65, 0.1), transparent)',
    noise: 'url("data:image/svg+xml,%3Csvg viewBox=\'0 0 200 200\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Cfilter id=\'noiseFilter\'%3E%3CfeTurbulence type=\'fractalNoise\' baseFrequency=\'0.9\' numOctaves=\'4\' stitchTiles=\'stitch\'/%3E%3C/filter%3E%3Crect width=\'100%25\' height=\'100%25\' filter=\'url(%23noiseFilter)\' opacity=\'0.05\'/%3E%3C/svg%3E")',
  },
};

export type Theme = typeof theme;
