`;

interface Round {
  number: number;
  thought: string;
  action: string;
  content: string;
  output: string;
}

const parseHistory = (history: string): Round[] => {
  const rounds: Round[] = [];
  const roundRegex = /=== Round (\d+) ===([\s\S]*?)(?==== Round|$)/g;

  let match;
  while ((match = roundRegex.exec(history)) !== null) {
    const roundNumber = parseInt(match[1]);
    const roundContent = match[2];

    const thought = roundContent.match(/🧠 Agent thought: (.*?)(?=\n⚡)/s)?.[1]?.trim() || '';
    const action = roundContent.match(/⚡ Agent action: (.*?)(?=\n📝)/s)?.[1]?.trim() || '';
    const content = roundContent.match(/📝 Agent content: (.*?)(?=\n💻)/s)?.[1]?.trim() || '';
    const output = roundContent.match(/💻 Environment output: ([\s\S]*?)(?=\n\n|$)/)?.[1]?.trim() || '';

    rounds.push({
      number: roundNumber,
      thought,
      action,
      content,
      output
    });
  }

  return rounds;
};

export const Demo: React.FC = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentRound, setCurrentRound] = useState(0);
  const [speed, setSpeed] = useState(2000); // milliseconds per step
  const [demoData, setDemoData] = useState<Round[]>([]);
  const terminalRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Load and parse demo data
    import('../data/demo.json').then((data) => {
      const history = data.default[0].history;
      const parsedRounds = parseHistory(history);
      setDemoData(parsedRounds);
    });
  }, []);

  useEffect(() => {
    if (isPlaying && currentRound < demoData.length - 1) {
      const timer = setTimeout(() => {
        setCurrentRound(prev => prev + 1);
      }, speed);
      return () => clearTimeout(timer);
    } else if (currentRound >= demoData.length - 1) {
      setIsPlaying(false);
    }
  }, [isPlaying, currentRound, demoData.length, speed]);

  useEffect(() => {
    // Auto-scroll terminal to bottom
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [currentRound]);

  const handlePlay = () => {
    if (currentRound >= demoData.length - 1) {
      setCurrentRound(0);
    }
    setIsPlaying(true);
  };

  const handlePause = () => {
    setIsPlaying(false);
  };

  const handleReset = () => {
    setIsPlaying(false);
    setCurrentRound(0);
  };

  const progress = demoData.length > 0 ? ((currentRound + 1) / demoData.length) * 100 : 0;

  return (
    <DemoSection id="demo">
      <SectionTitle>Interactive Attack Demo</SectionTitle>
      <SectionSubtitle>
        Watch how the agent executes a DNS spoofing attack through terminal interactions
      </SectionSubtitle>

      <DemoContainer>
        <DemoHeader>
          <DemoInfo>
            <InfoItem>
              <InfoLabel>Attack Type</InfoLabel>
              <InfoValue>DNS Spoofing</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Steps</InfoLabel>
              <InfoValue>{currentRound + 1} / {demoData.length}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Status</InfoLabel>
              <InfoValue style={{ color: currentRound === demoData.length - 1 ? '#10b981' : '#f59e0b' }}>
                {currentRound === demoData.length - 1 ? 'Completed' : 'In Progress'}
              </InfoValue>
            </InfoItem>
          </DemoInfo>

          <ControlButtons>
            <ControlButton onClick={handlePlay} disabled={isPlaying}>
              <span>▶️</span> Play
            </ControlButton>
            <ControlButton onClick={handlePause} disabled={!isPlaying}>
              <span>⏸️</span> Pause
            </ControlButton>
            <ControlButton onClick={handleReset}>
              <span>🔄</span> Reset
            </ControlButton>
            <ControlButton onClick={() => setSpeed(speed === 2000 ? 1000 : speed === 1000 ? 500 : 2000)}>
              <span>⚡</span> {speed === 2000 ? '1x' : speed === 1000 ? '2x' : '4x'}
            </ControlButton>
          </ControlButtons>
        </DemoHeader>

        <Terminal ref={terminalRef}>
          <TerminalContent>
            <AnimatePresence>
              {demoData.slice(0, currentRound + 1).map((round, index) => (
                <RoundContainer
                  key={round.number}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                >
                  <RoundHeader>=== Round {round.number} ===</RoundHeader>

                  <ThoughtBlock
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.2 }}
                  >
                    <strong>🧠 Agent thought:</strong> {round.thought}
                  </ThoughtBlock>

                  <ActionBlock
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.4 }}
                  >
                    <strong>⚡ Agent action:</strong> {round.action}
                    {round.content && round.content !== 'None' && (
                      <div style={{ marginTop: '0.5rem' }}>
                        <strong>📝 Content:</strong>
                        <SyntaxHighlighter 
                          language="bash" 
                          style={vscDarkPlus}
                          customStyle={{
                            margin: '0.5rem 0',
                            padding: '0.5rem',
                            fontSize: '0.875rem',
                            borderRadius: '4px'
                          }}
                        >
                          {round.content}
                        </SyntaxHighlighter>
                      </div>
                    )}
                  </ActionBlock>

                  <OutputBlock
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.6 }}
                  >
                    <strong>💻 Environment output:</strong>
                    <pre style={{ margin: '0.5rem 0', fontSize: '0.8rem' }}>
                      {round.output || 'No output'}
                    </pre>
                  </OutputBlock>
                </RoundContainer>
              ))}
            </AnimatePresence>
          </TerminalContent>
        </Terminal>

        <ProgressBar>
          <ProgressFill
            initial={{ width: '0%' }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.3 }}
          />
        </ProgressBar>
      </DemoContainer>
    </DemoSection>
  );
};
